mapper.select:
select no, title, member_no, member_name,view_cnt, reg_date, group_no, order_no, depth from (SELECT * FROM ( SELECT rownum rnum, no, title, member_no, member_name,view_cnt, reg_date, group_no, order_no, depth FROM ( select a.no no, a.title title, a.content as content, a.member_no member_no, b.name as member_name, a.view_cnt view_cnt, to_char(a.reg_date, 'yyyy-mm-dd hh24:mi:ss') as reg_date, a.group_no group_no, a.order_no order_no, a.depth depth from board a, member b where a.member_no = b.no order by group_no desc, order_no asc, depth asc)
where title like '%${kwd }%' or content like '%${kwd }%'
where member_name like '%${kwd }%'
) pagetable where rnum <= #{end }) where rnum >= #{start }
